$\text{tan}\left({\dfrac{\pi}{6}}\right)=\text{tan}\left({\dfrac{\pi}{2}-\dfrac{\pi}{3}}\right)= \text{cot}{\dfrac{\pi}{3}}, \ \text{sec}\left({\dfrac{\pi}{4}}\right)=\text{sec}\left({\dfrac{\pi}{2}-\dfrac{\pi}{4}}\right)= \text{cosec}{\dfrac{\pi}{4}}$
\end{example}	

\begin{example}  
$\text{sin}\left({\dfrac{2\pi}{3}}\right)=\text{sin}\left({\dfrac{\pi}{2}+\dfrac{\pi}{6}}\right)= \text{cos}{\dfrac{\pi}{6}}=\dfrac{\sqrt{3}}{2}$

$\text{cos}\left({\dfrac{3\pi}{4}}\right)=\text{cos}\left({\dfrac{\pi}{2}+\dfrac{\pi}{4}}\right)= -\text{sin}{\dfrac{\pi}{4}}=-\dfrac{1}{\sqrt{2}}$

$\text{tan}\left({\dfrac{5\pi}{6}}\right)=\text{tan}\left({\dfrac{\pi}{2}+\dfrac{\pi}{3}}\right)= -\text{cot}{\dfrac{\pi}{3}}=-\dfrac{1}{\sqrt{3}}$
\end{example}

\begin{example}
$\text{sin}\left(\dfrac{4\pi}{3}\right)=\text{sin}\left(\pi+\dfrac{\pi}{3}\right)=-\text{sin}\left(\dfrac{\pi}{3}\right)=-\dfrac{\sqrt{3}}{2}$

$\text{cos}\left(\dfrac{5\pi}{4}\right)=\text{cos}\left(\pi+\dfrac{\pi}{4}\right)=-\text{cos}\left(\dfrac{\pi}{4}\right)=-\dfrac{1}{\sqrt{2}}$

$\text{tan}\left(\dfrac{7\pi}{6}\right)=\text{tan}\left(\pi+\dfrac{\pi}{6}\right)=\text{tan}\left(\dfrac{\pi}{6}\right)=\dfrac{1}{\sqrt{3}}$
\end{example}

